import osFILE_NAME = "students.txt"def add_student():    while True:        try:            roll=int(input("enter roll no: ").strip()) #Q1            break        except ValueError: #Q1            print("roll number must be valid integer")    while True:        name=input("enter name: ").strip()        if not name: #Q1            print("name cannot be empty, please enter a name.")        else:            break    while True:        try:            marks=float(input("enter marks: ").strip()) #Q1            if marks <0 or marks >100: #Q1                print("marks must be between 0 and 100.")                continue            break        except ValueError: #Q1            print("marks must be a number and cannot be empty.")    try:        with open(FILE_NAME,'a') as f: #Q1            f.write(f"{roll},{name},{marks}\n")        print("student added")    except OSError as e: #Q1        print("file error:", e)    except Exception as e: #Q1        print("unexpected error",e)#add_student()def view_student():    try:        if not os.path.exists(FILE_NAME):            raise FileNotFoundError("file not found") #Q1        with open(FILE_NAME,'r') as f:            data=f.readlines()#        if not data:            print("No student data found!")            return        print("\n--- Student Records ---")        for i in data:            raw = i.strip()            if not raw: #Q3                print("[INVALID] <blank line>")                continue            parts = raw.split(',')            if len(parts) != 3: #Q3                print(f"[INVALID FORMAT]")                continue            roll = parts[0].strip()            name = parts[1].strip()            marks = parts[2].strip()            try:                float(marks) #Q3/Q1                print(f"Roll: {roll} | Name: {name} | Marks: {marks}")            except ValueError:#Q3/Q1                print(f"Roll: {roll} | Name: {name} | Marks: {marks} <-- INVALID MARKS")    except FileNotFoundError: #Q1        print("file not found")    except Exception as e: #Q1        print("Error:",e)#view_student()def search_student():    try:        if not os.path.exists(FILE_NAME):            raise FileNotFoundError("file not found") #Q1        with open(FILE_NAME, 'r') as f:            students = f.readlines()        while True:            while True:                roll_num_search = input("enter roll no: ").strip()                if not roll_num_search: #Q1                    print("Roll number cannot be empty")                    continue                break            found = False            for line in students:                raw = line.strip()                if not raw: #Q3                    continue                parts = raw.split(',')                if len(parts) != 3: #Q3                    continue                roll = parts[0].strip()                name = parts[1].strip()                marks = parts[2].strip()                if roll_num_search == roll:                    print("\nRecord Found:")                    print(f"Name: {name}")                    try:                        float(marks) #Q3/1                        print(f"Marks: {marks}")                    except ValueError: #Q3/1                        print(f"Marks: {marks}  <-- INVALID MARKS")                    found = True                    break            if found:                break            else:                print("student record not found, try again\n") #Q1    except FileNotFoundError: #Q1        print("File not found!")    except Exception as e: #Q1        print("error", e)#search_student()def update_student():    try:        # ---- roll input loop ----        while True:            roll_search = input("enter roll no: ").strip()            if not roll_search: #Q1                print("Roll number cannot be empty")                continue            break        if not os.path.exists(FILE_NAME):            raise FileNotFoundError("file not found") #Q1        with open(FILE_NAME, "r") as f:            students = f.readlines()        # ---- find current record (for display) ----        current_record = None        for line in students:            raw = line.strip()            if not raw: #Q3                continue            parts = raw.split(",")            if len(parts) != 3: #Q3                continue            roll = parts[0].strip()            if roll == roll_search:                name = parts[1].strip()                marks = parts[2].strip()                current_record = f"{roll},{name},{marks}"                break        if not current_record:            print("student record not found")            return        print("\nCurrent Record:")        print(current_record)        proceed = input("Do you want to update this record? (y/n): ").strip().lower()        if proceed != "y":            print("Update cancelled.")            return        # ---- new name ----        while True:            new_name = input("enter new name: ").strip()            if not new_name: #Q1                print("Name cannot be empty")                continue            break        new_marks = None        while True:            try:                new_marks = float(input("enter new marks: ").strip()) #Q4            except ValueError:                print("marks must be a number") #Q4                continue            if new_marks < 0 or new_marks > 100: #Q4                print("marks must be between 0 and 100")                continue            break        new_record = f"{roll_search},{new_name},{new_marks}"        print("\n--- Update Preview ---")        print("Before:", current_record)        print("After :", new_record)        confirm = input("Are you sure you want to update? (y/n): ").strip().lower()        if confirm != "y":            print("Update cancelled.")            return        new_lines = []        updated = False        for line in students:            raw = line.strip()            parts = raw.split(",")            if len(parts) != 3: #Q3                new_lines.append(line)  # keep as-is                continue            roll = parts[0].strip()            if roll == roll_search and not updated:                new_lines.append(new_record + "\n")  #Q1                updated = True            else:                new_lines.append(line)  # keep original line        if not updated:            print("No update done.")            return        with open(FILE_NAME, "w") as f:            f.writelines(new_lines)        print("Data updated successfully.")    except FileNotFoundError: #Q1        print("File not found!")    except Exception as e: #Q1        print("Error:", e)#update_student()def delete_student(): #Q2    try:        # ---- roll input loop ----        while True:            roll_search = input("enter roll no to delete: ").strip()            if not roll_search: #Q1                print("Roll number cannot be empty")                continue            break        if not os.path.exists(FILE_NAME):            raise FileNotFoundError("file not found") #Q1        with open(FILE_NAME, "r") as f:            students = f.readlines()        # ---- find current record (for display) ----        current_record = None        for line in students:            raw = line.strip()            if not raw: #Q3                continue            parts = raw.split(",")            if len(parts) != 3: #Q3                continue            roll = parts[0].strip()            if roll == roll_search:                name = parts[1].strip()                marks = parts[2].strip()                current_record = f"{roll},{name},{marks}"                break        if not current_record:            print("student record not found (nothing deleted)")            return        print("\nRecord to Delete:")        print(current_record)        confirm = input("Are you sure you want to delete? (y/n): ").strip().lower()        if confirm != "y":            print("Delete cancelled.")            return        new_lines = []        deleted = False        for line in students:            raw = line.strip()            parts = raw.split(",")            if len(parts) != 3: #Q3                new_lines.append(line)  # keep as-is                continue            roll = parts[0].strip()            if roll == roll_search and not deleted:                deleted = True                continue  # skip this line (delete)            else:                new_lines.append(line)        if not deleted:            print("student record not found during delete (no changes made)")            return        with open(FILE_NAME, "w") as f:            f.writelines(new_lines)        print("Record deleted successfully.")    except FileNotFoundError:        print("File not found!")    except Exception as e:        print("Error:", e)#delete_student()def return_to_main():    while True:        print("\n1. Return to Main Menu")        print("2. Exit")        choice = input("Enter your choice: ").strip()        if choice == "1":            return        elif choice == "2":            print("exit")            exit()        else:            print("invalid choice")def main():    while True:        print("\n1. Add Student")        print("2. View Students")        print("3. Search Student")        print("4. Update Student")        print("5. Delete Student")        print("6. Exit")        choice=input("enter your choice:").strip()        if choice=="1":            add_student()            return_to_main()        elif choice=="2":            view_student()            return_to_main()        elif choice=="3":            search_student()            return_to_main()        elif choice=="4":            update_student()            return_to_main()        elif choice=="5":            delete_student()            return_to_main()        elif choice=="6":            print("exit")            break        else:            print("invalid choice")main()"""1] Add proper exception handling in Search, Update, and View functionsfile not foundblank/corrupted linesinvalid input values2] Create a Delete functionWhen the user enters a roll number, remove that student record from the file.3] Skip corrupted or incomplete file linesThe program must not crash if file content is malformed.4] Validate marks input in the Update functionIf marks are not numeric, show an error instead of crashing."""